function generateMaintenancePDF(data) {
  const doc = new jsPDF();
  doc.setFontSize(16);
  doc.text("SOAR TN - Maintenance Request", 20, 20);

  doc.setFontSize(11);
  doc.text(`Ticket: ${data.ticket}`, 20, 35);
  doc.text(`Requested By: ${data.name}`, 20, 45);
  doc.text(`House: ${data.house}`, 20, 55);
  doc.text(`Contact: ${data.contact}`, 20, 65);

  doc.text("Work Description:", 20, 80);
  doc.text(data.work, 20, 90, { maxWidth: 170 });

  doc.text("----- Maintenance Use Only -----", 20, 130);
  doc.text("Materials:", 20, 145);
  doc.text("Cost:", 20, 155);
  doc.text("Assigned To:", 20, 165);
  doc.text("Completed Date:", 20, 175);

  doc.save(`${data.ticket}.pdf`);
}

